const express = require('express');
const path = require('path');
const { createProxyMiddleware } = require('http-proxy-middleware');

const app = express();
const port = process.env.PORT || 8080;

app.set('trust proxy', true);
app.use(express.json());
app.use(express.urlencoded({ extended: true }));

// Logging
app.use((req, res, next) => {
    console.log(`${new Date().toISOString()} - ${req.method} ${req.originalUrl}`);
    next();
});

// CORS
app.use((req, res, next) => {
    res.header('Access-Control-Allow-Origin', '*');
    res.header('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    res.header('Access-Control-Allow-Headers', 'Content-Type, Authorization, X-Requested-With');
    if (req.method === 'OPTIONS') return res.sendStatus(200);
    next();
});

// Health check
app.get('/health', (req, res) => {
    res.json({ 
        status: 'ok',
        domain: 'sunshinee.online',
        timestamp: new Date().toISOString()
    });
});

// API proxy
app.use('/api', createProxyMiddleware({
    target: 'http://localhost:8090',
    changeOrigin: true,
    pathRewrite: { '^/api': '/telebook/api' },
    onError: (err, req, res) => {
        res.status(503).json({ success: false, error: 'PHP backend unavailable' });
    }
}));

// CRITICAL: Fix MIME types for all static files
app.use(express.static('dist', {
    setHeaders: (res, filePath) => {
        const ext = path.extname(filePath).toLowerCase();
        
        // Fix JavaScript MIME type - THIS IS THE KEY FIX
        if (ext === '.js' || ext === '.mjs') {
            res.setHeader('Content-Type', 'application/javascript; charset=utf-8');
        } else if (ext === '.css') {
            res.setHeader('Content-Type', 'text/css; charset=utf-8');
        } else if (ext === '.json') {
            res.setHeader('Content-Type', 'application/json; charset=utf-8');
        } else if (ext === '.ico') {
            res.setHeader('Content-Type', 'image/x-icon');
        } else if (ext === '.png') {
            res.setHeader('Content-Type', 'image/png');
        } else if (ext === '.svg') {
            res.setHeader('Content-Type', 'image/svg+xml; charset=utf-8');
        }
        
        // Prevent MIME type sniffing
        res.setHeader('X-Content-Type-Options', 'nosniff');
        
        console.log(`Serving: ${filePath} with Content-Type: ${res.getHeader('Content-Type')}`);
    }
}));

// Favicon fallback
app.get('/favicon.ico', (req, res) => {
    res.setHeader('Content-Type', 'image/x-icon');
    const faviconPath = path.join(__dirname, 'dist', 'favicon.ico');
    res.sendFile(faviconPath, (err) => {
        if (err) {
            const icon = Buffer.from('', 'base64');
            res.send(icon);
        }
    });
});

// SPA fallback
app.get('*', (req, res) => {
    const indexPath = path.join(__dirname, 'dist', 'index.html');
    res.sendFile(indexPath, (err) => {
        if (err) {
            res.status(404).send('<h1>TWeb - Build Required</h1><p>Run: pnpm run build:ignore</p>');
        }
    });
});

app.listen(port, '0.0.0.0', () => {
    console.log(`🌐 TWeb Server Fixed - Port ${port}`);
    console.log(`📱 URL: https://sunshinee.online`);
});
